import React, { useState } from 'react';

function App() {
  const [goal, setGoal] = useState('');
  const [plan, setPlan] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleGenerate = async () => {
    if (!goal) {
      alert('Please enter a goal');
      return;
    }

    setLoading(true);
    setError('');
    setPlan(null);

    try {
      const response = await fetch('http://localhost:4000/api/plan/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ goal }),
      });

      const data = await response.json();
      if (response.ok) {
        setPlan(data.plan);
      } else {
        setError(data.error || 'Something went wrong');
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      style={{
        minHeight: '100vh',
        padding: '50px',
        background: 'linear-gradient(to bottom, #3a7bd5, #00d2ff)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        fontFamily: 'Arial, sans-serif',
        color: '#fff',
      }}
    >
      <h1 style={{ fontSize: '3rem', marginBottom: '30px' }}>
        Welcome to Smart Task Planner
      </h1>

      <input
        type="text"
        placeholder="Enter your goal..."
        value={goal}
        onChange={(e) => setGoal(e.target.value)}
        style={{
          padding: '12px 20px',
          width: '400px',
          borderRadius: '8px',
          border: 'none',
          marginBottom: '20px',
          fontSize: '1rem',
        }}
      />

      <button
        onClick={handleGenerate}
        style={{
          padding: '12px 30px',
          borderRadius: '8px',
          border: 'none',
          background: '#ff7e5f',
          color: '#fff',
          fontSize: '1rem',
          cursor: 'pointer',
          marginBottom: '40px',
        }}
      >
        {loading ? 'Generating...' : 'Generate Task Plan'}
      </button>

      {error && <p style={{ color: '#ffdddd' }}>{error}</p>}

      {plan && (
        <div
          style={{
            background: 'rgba(255, 255, 255, 0.1)',
            padding: '20px',
            borderRadius: '12px',
            width: '90%',
            maxWidth: '800px',
          }}
        >
          <h2 style={{ marginBottom: '10px' }}>Generated Task Plan</h2>
          <p>
            <strong>Start Date:</strong> {plan.recommended_start_date} <br />
            <strong>End Date:</strong> {plan.recommended_end_date}
          </p>

          <ul style={{ listStyle: 'none', padding: 0 }}>
            {plan.tasks.map((task) => (
              <li
                key={task.id}
                style={{
                  margin: '15px 0',
                  padding: '10px',
                  borderRadius: '8px',
                  background: 'rgba(0,0,0,0.2)',
                }}
              >
                <strong>{task.title}</strong> (Estimate: {task.estimate_days} days)
                {task.depends_on && task.depends_on.length > 0 && (
                  <div style={{ fontSize: '0.9rem', color: '#ffebcd' }}>
                    Depends on: {task.depends_on.join(', ')}
                  </div>
                )}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
